import React from "react";
import ProductDisplay from "./ProductDisplay";

const NewArrivals = () => {
  const newArrivals = [
    {
      id: 5,
      name: "Asus Tuf gaming",
      image: "data:image/jpeg;base64,", 
      price: "$999.99",
      description:
        "Bristling with high-refresh rate displays and competitive GPUs.",
    },
    {
      id: 6,
      name: "Asus Vivobook 16",
      image: "data:image/jpeg;base64,", 
      price: "$799.99",
      description:
        "Vivobook 16 helps you complete all your tasks faster than ever, whether it's at work, at home, or out and about.",
    },
    {
      id: 7,
      name: "Google Pixel 8",
      image: "data:image/jpeg;base64,", 
      price: "$799.99",
      description:
        "Built with Pixel's most advanced chip and Google AI to help you do more, effortlessly. Clear, vivid Actua display, even in direct sunlight. ",
    },
    {
      id: 8,
      name: "Samsung s23 ultra",
      image: "data:image/jpeg;base64,", 
      price: "$999.99",
      description:
        "The Samsung Galaxy S23 Ultra is the headliner of the S23 series. Specifications are top-notch including 6.8-inch Dynamic AMOLED display with 120Hz refresh rate",
    },
  ];

  return (
    <div className="mt-8 text-center">
      <h2 className="text-2xl font-bold mb-4">New Arrivals</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {newArrivals.map((product) => (
          <ProductDisplay
            key={product.id}
            name={product.name}
            image={product.image}
            price={product.price}
            description={product.description}
          />
        ))}
      </div>
    </div>
  );
};

export default NewArrivals;
